package edgeos

import (
	"bytes"
	"testing"

	. "github.com/britannic/testutils"
)

func TestConfigString(t *testing.T) {
	c, err := ReadCfg(bytes.NewBufferString(Cfg))
	OK(t, err)
	NewParms(c).SetOpt(
		Dir("/tmp"),
		Ext("blacklist.conf"),
		Method("GET"),
		Nodes([]string{"domains", "hosts"}),
		STypes([]string{"pre-configured", "files", "urls"}),
	)
	Equals(t, JSONcfg, c.String())
}

